    var magnitude;
    var rand = field.random(0, 100);
    if (rand < 5) {
        this.power_ = 10;
        magnitude = 4;
    } else if (rand < 15) {
        this.power_ = 30;
        magnitude = 5;
    } else if (rand < 35) {
        this.power_ = 50;
        magnitude = 6;
    } else if (rand < 65) {
        this.power_ = 70;
        magnitude = 7;
    } else if (rand < 85) {
        this.power_ = 90;
        magnitude = 8;
    } else if (rand < 95) {
        this.power_ = 110;
        magnitude = 9;
    } else {
        this.power_ = 150;
        magnitude = 10;
    }
    field.print(Text.battle_messages_unique(14, magnitude));
};

